export default {
	Tune_Update: () => {
		let state = 1;
		if(Select_Wheel.selectedOptionLabel === "")
		{
			showAlert('请选择一个轮毂样式');
			state = 0;
		}

		if(Select_Aero.selectedOptionLabel === "")
		{
			showAlert('请选择一个包围样式');
			state = 0;
		}

		if(Select_Bonnet.selectedOptionLabel === "")
		{
			showAlert('请选择一个引擎盖样式');
			state = 0;
		}

		if(Select_Mirror.selectedOptionLabel === "")
		{
			showAlert('请选择一个后视镜样式');
			state = 0;
		}

		if(Select_Neon.selectedOptionLabel === "")
		{
			showAlert('请选择一个车底灯样式');
			state = 0;
		}

		if(Select_Plate.selectedOptionLabel === "")
		{
			showAlert('请选择一个车牌灯样式');
			state = 0;
		}

		if(Select_Wing.selectedOptionLabel === "")
		{
			showAlert('请选择一个尾翼样式');
			state = 0;
		}

		if(Select_Trunk.selectedOptionLabel === "")
		{
			showAlert('请选择一个后尾箱样式');
			state = 0;
		}

		if(state === 0)
		{
			return "不执行上传指令"
		}
		else if(state === 1)
		{
			showAlert('执行');
			Update_Car.run();
			showAlert('已提交修改，请到游戏内确认');
			Select_Car_Tune_NoDe.run(); //更新数据
		}
	}
}
