actionConfiguration.body:
() => {
  let Cheat = 0;
  const InputNameText = InputName.text;
  for (const char of InputNameText) {
    const charCode = char.charCodeAt(0);
    if (charCode >= 0x3040 && charCode <= 0x30FF || charCode >= 0x31F0 && charCode <= 0x31FF || charCode >= 0xFF21 && charCode <= 0xFF3A || charCode >= 0xFF41 && charCode <= 0xFF5A || charCode >= 0xFF01 && charCode <= 0xFF5E) {
      continue;
    } else {
      Cheat = 1;
      showAlert('检测到名称部分输入的字符非全角字符，已中止修改', 'error');
      break;
    }
  }
  if (InputName.text.length > 5) {
    Cheat = 1;
    showAlert('检测到 名称 部分超出限定的五位，已中止修改', 'error');
  }
  const InputLevelValue = Select_Level.selectedOptionValue;
  if (InputLevelValue == 0 || InputLevelValue > 20) {
    if (InputLevelValue != Select_Car_Detail.data[0].level) {
      Cheat = 1;
      showAlert('检测到 等级 部分数值设置超出规定范围，已取消修改操作', 'error');
    }
  }
  const InputOdoText = InputOdometer.text;
  if (InputOdoText > 50000) {
    if (InputOdoText != Select_Car_Detail.data[0].odometer) {
      Cheat = 1;
      showAlert('检测到 公里数 部分数值设置超出规定范围，已取消修改操作', 'error');
    }
  }
  if (InputTitle.text.length > 8) {
    Cheat = 1;
    showAlert('检测到 称号 部分超出限定的八位字符，已中止修改', 'error');
  }
  const InputTropyText = InputRgTropy.text;
  if (InputTropyText > 200) {
    if (InputTropyText != Select_Car_Detail.data[0].rgTrophy) {
      Cheat = 1;
      showAlert('检测到 化身奖杯数量 部分数值设置超出规定范围，已取消修改操作', 'error');
    }
  }
  const InputPlateText = InputPlateNumber.text;
  if (InputPlateText > 9999) {
    if (InputPlateText != Select_Car_Detail.data[0].plateNumber) {
      Cheat = 1;
      showAlert('检测到 车牌号 部分数值设置超出规定范围（0 ~ 9999），已取消修改操作', 'error');
    }
  }
  const SelectArea = SelectRegionId.selectedOptionValue;
  if (SelectArea < 1 || SelectArea > 47) {
    Cheat = 1;
    showAlert('检测到 车辆登记区域 部分选择数据异常，已取消修改操作', 'error');
  }
  const SelectWheel = Select_Wheel.selectedOptionValue;
  if (SelectWheel == 10 || SelectWheel == 25 || SelectWheel == 26 || SelectWheel == 35 || SelectWheel == 36 || SelectWheel == 44 || SelectWheel == 45 || SelectWheel == 46 || SelectWheel == 52) {
    Cheat = 1;
    showAlert('检测到 轮毂 部分选择数据异常，已取消修改操作', 'error');
  }
  const InputCColor = InputCustomColor.text;
  if (InputCColor > 40) {
    Cheat = 1;
    showAlert('检测到 车辆自定义颜色 部分选择数据异常，已取消修改操作', 'error');
  }
  if (Cheat === 0) {
    Update_Car_Data.run();
    Select_Car_Detail.run();
    InputName.text;
    showAlert('修改成功', 'success');
  } else {
    showAlert('请检查您是否有遵循规则填写数值', 'error');
  }
  return Cheat;
}

